import React, { Component } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import axios from "axios";

export default class EditProduct extends Component {
  constructor(props) {
    super(props);

    this.onChangeExpenseName = this.onChangeExpenseName.bind(this);
    this.onChangeExpenseAmount = this.onChangeExpenseAmount.bind(this);
    this.onChangeExpenseDescription =
      this.onChangeExpenseDescription.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    // State
    this.state = {
      name: "",
      amount: "",
      description: "",
    };
  }

  componentDidMount() {
    axios
      .get("http://localhost:8000/api/products/" + this.props.match.params.id)
      .then((res) => {
        console.log(res);
        this.setState({
          name: res.data.title,
          amount: res.data.price,
          description: res.data.description,
        });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  onChangeExpenseName(e) {
    this.setState({ name: e.target.value });
  }

  onChangeExpenseAmount(e) {
    this.setState({ amount: e.target.value });
  }

  onChangeExpenseDescription(e) {
    this.setState({ description: e.target.value });
  }

  onSubmit(e) {
    e.preventDefault();

    const expenseObject = {
      name: this.state.name,
      amount: this.state.amount,
      description: this.state.description,
    };

    axios
      .put(
        "http://localhost:8000/api/products/" + this.props.match.params.id,
        expenseObject
      )
      .then((res) => {
        console.log(res.data);
        console.log("product successfully updated");
      })
      .catch((error) => {
        console.log(error);
      });

    // Redirect to Expense List
    this.props.history.push("/product-listing");
  }

  render() {
    return (
      <div className="form-wrapper left-text">
        <h4>Update Product</h4>
        <br />
        <Form onSubmit={this.onSubmit}>
          <Form.Group controlId="Name">
            <Form.Label>Name</Form.Label>
            <Form.Control
              type="text"
              value={this.state.name}
              onChange={this.onChangeExpenseName}
            />
          </Form.Group>

          <Form.Group controlId="Amount">
            <Form.Label>Amount</Form.Label>
            <Form.Control
              type="number"
              value={this.state.amount}
              onChange={this.onChangeExpenseAmount}
            />
          </Form.Group>

          <Form.Group controlId="Description">
            <Form.Label>Description</Form.Label>
            <Form.Control
              type="text"
              value={this.state.description}
              onChange={this.onChangeExpenseDescription}
            />
          </Form.Group>

          <Button variant="danger" size="lg" block="block" type="submit">
            Update Expense
          </Button>
        </Form>
      </div>
    );
  }
}
