import React, { Component } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import axios from "axios";

export default class EditProduct extends Component {
  constructor(props) {
    super(props);

    this.onChangeProductName = this.onChangeProductName.bind(this);
    this.onChangeProductAmount = this.onChangeProductAmount.bind(this);
    this.onChangeProductDescription =
      this.onChangeProductDescription.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    // State
    this.state = {
      name: "",
      amount: "",
      description: "",
    };
  }

  componentDidMount() {
    axios
      .get("http://localhost:8000/api/products/" + this.props.match.params.id)
      .then((res) => {
        console.log(res);
        this.setState({
          name: res.data.title,
          amount: res.data.price,
          description: res.data.description,
        });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  onChangeProductName(e) {
    this.setState({ name: e.target.value });
  }

  onChangeProductAmount(e) {
    this.setState({ amount: e.target.value });
  }

  onChangeProductDescription(e) {
    this.setState({ description: e.target.value });
  }

  onSubmit(e) {
    e.preventDefault();

    const productObject = {
      name: this.state.name,
      amount: this.state.amount,
      description: this.state.description,
    };

    axios
      .put(
        "http://localhost:8000/api/products/" + this.props.match.params.id,
        productObject
      )
      .then((res) => {
        console.log(res.data);
        console.log("product successfully updated");
      })
      .catch((error) => {
        console.log(error);
      });

    // Redirect to List
    this.props.history.push("/product-listing");
  }

  render() {
    return (
      <div className="form-wrapper">
        <h4>Update Product</h4>
        <br />
        <Form onSubmit={this.onSubmit}>
          <Form.Group controlId="Name">
            <Form.Label>Name</Form.Label>
            <Form.Control
              type="text"
              value={this.state.name}
              onChange={this.onChangeProductName}
            />
          </Form.Group>

          <Form.Group controlId="Amount">
            <Form.Label>Amount</Form.Label>
            <Form.Control
              type="number"
              value={this.state.amount}
              onChange={this.onChangeProductAmount}
            />
          </Form.Group>

          <Form.Group controlId="Description">
            <Form.Label>Description</Form.Label>
            <Form.Control
              type="text"
              value={this.state.description}
              onChange={this.onChangeProductDescription}
            />
          </Form.Group>
          <Form.Group controlId="image">
            <Form.Label>Image</Form.Label>
            <br />
            <Form.Control
              as="input"
              type="file"
              required
              value={this.state.image}
              onChange={this.onChangeProductImage}
            />
          </Form.Group>
          <br />
          <Form.Group controlId="imageShow">
            <img
              height="150px"
              src={this.state.image}
              onChange={this.onChangeProductImage}
            />
          </Form.Group>
          <br />
          <Button variant="danger" size="lg" block="block" type="submit">
            Update Product
          </Button>
        </Form>
      </div>
    );
  }
}
